// Badge component
function Badge({ text, color = '#667eea' }) {
  return (
    <span
      style={{
        display: 'inline-block',
        padding: '4px 12px',
        backgroundColor: color,
        color: 'white',
        borderRadius: '12px',
        fontSize: '12px',
        fontWeight: 'bold',
        marginTop: '8px',
      }}
    >
      {text}
    </span>
  );
}

// HeroCard component
function HoloCard({
  name,
  power,
  image,
  country,
}) {
  const handleFavorite = () => {
    console.log(`${name} added to favorites!`);
    alert(`${name} added to favorites!`);
  };
  const handleViewDetails = () => {
    console.log('Hero Details:', { name, country, power});
    alert(`Viewing details for ${name}`);
  };

  const handleMouseEnter = (e) => {
    e.currentTarget.style.transform = 'scale(1.05)';
    e.currentTarget.style.backgroundColor = 'rgba(59, 255, 33, 0.36)';
  };

  // Event handler for mouse leave
  const handleMouseLeave = (e) => {
    e.currentTarget.style.transform = 'scale(1)';
    e.currentTarget.style.backgroundColor = 'rgba(255, 255, 255, 0.1)';
  };
  // TODO: Create event handler functions here
  // Hint: const handleFavorite = () => { /* code here */ }
  // Hint: You can use alert() to show a message
  // Hint: You can use console.log() to log to the console

  const cardStyle = {
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    borderRadius: '12px',
    padding: '20px',
    backdropFilter: 'blur(10px)',
    border: '1px solid rgba(255, 255, 255, 0.2)',
    boxShadow: '0 8px 32px rgba(0, 0, 0, 0.2)',
    transition: 'transform 0.3s',
  };

  const imageStyle = {
    width: '100%',
    height: '200px',
    objectFit: 'cover',
    borderRadius: '8px',
    marginBottom: '15px',
  };

  const buttonStyle = {
    padding: '8px 16px',
    margin: '5px',
    fontSize: '14px',
    backgroundColor: '#fff',
    color: '#667eea',
    border: 'none',
    borderRadius: '6px',
    cursor: 'pointer',
    fontWeight: 'bold',
    transition: 'all 0.2s',
  };

  return (
    <div style={cardStyle}
    onMouseEnter={handleMouseEnter}
    onMouseLeave={handleMouseLeave}>
      <img src={image} alt={name} style={imageStyle} />
      <h2
        style={{
          color: 'white',
          fontSize: '24px',
          margin: '0 0 8px 0',
        }}
      >
        {name}
      </h2>
      {country && (
        <p
          style={{
            color: 'rgba(255, 255, 255, 0.6)',
            fontSize: '12px',
            fontStyle: 'italic',
            margin: '0 0 8px 0',
          }}
        >
          from {country}
        </p>
      )}
      <p
        style={{
          color: 'rgba(255, 255, 255, 0.8)',
          fontSize: '14px',
          margin: '0 0 8px 0',
        }}
      >
        {power}
      </p>
      <button style={buttonStyle} onClick={handleFavorite}>
          Favorite
        </button>
        <button style={buttonStyle} onClick={handleViewDetails}>
          View Details
        </button>
      <div style={{ marginTop: '15px' }}>
        {/* TODO: Add a Favorite button with onClick */}
        {/* Hint: <button onClick={yourFunction}>Text</button> */}

        {/* TODO: Add a View Details button with onClick */}

        {/* BONUS: Add hover effect to the card */}
        {/* Hint: Use onMouseEnter and onMouseLeave on the card div */}
        {/* Hint: You can modify the element's style directly with e.target.style */}
        
      </div>
    </div>
  );
}

